    private void jButton16ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton16ActionPerformed
        // ======================
        if(!equalPressed){
        lastOperator=false;
        msg=msg+"|"+getDisplayString();
        System.out.println(msg);
        
        dotPutted=false;
        
        
        
        setDisplayString(makeRequest(msg));
        
        msg="";
        equalPressed=true;
        }
        
        if(sctcOperation){
            setDisplayString(makeRequest(getDisplayString()));
        }
        
    }//GEN-LAST:event_jButton16ActionPerformed

