    public String makeRequest(String message){
	String response = null;
	try {
	      action = RPCClient.getInstance(); 
	      System.out.println(" [x] Requesting " + message);   
	      response = action.call(message);
	      System.out.println(" [.] Result: '" + response + "'");
	    }
	    catch  (Exception e) {
	      e.printStackTrace();
	    }
	    
        if(response.endsWith(".0")) response=response.substring(0, response.length()-2);
	   return response;
    }
    
    
    
    
    public String removeLastChar(String str) {
    if (str.length() > 0) {
      str = str.substring(0, str.length()-1);
    }
    return str;
    }
    
    public void addOperator(String op){
        msg=op+getDisplayString();
    }
    
    public void clearDisplay(){
        jTextField1.setText("");
        equalPressed=false;
        lastOperator=false;
    }
    
    public void setDisplayString(String s){
		jTextField1.setText(s);
	}

    public String getDisplayString (){
		return jTextField1.getText();
	}
    
    public void addDigitToDisplay(String s){
        String ss=getDisplayString()+s;
        setDisplayString(ss);
    }
        

